	private static RenderFragment RenderIcon(string viewBox, PathInfo[] data, string w = "24px", string h = "24px") {
		return builder => {
			builder.OpenElement(0, "svg");
			builder.AddAttribute(1, "width", w);
			builder.AddAttribute(2, "height", h);
			builder.AddAttribute(3, "xmlns", "http://www.w3.org/2000/svg");
			builder.AddAttribute(4, "viewBox", viewBox);
			var x = 5; // :)
			foreach (var i in data) {
				builder.AddContent(x++, RenderPath(i));
			}
			builder.CloseElement();
		};
	}
	
	private static RenderFragment RenderPath(PathInfo info) {
		return builder => {
			builder.OpenElement(0, "path");
			builder.AddAttribute(1, "d", info.Data);
			if (!string.IsNullOrEmpty(info.Class)) {
				builder.AddAttribute(2, "class", info.Class);
			}
			builder.CloseElement();
		};
	}
	
	private record struct PathInfo(string Data, string? Class = null);
